// Component to render icon based on MIME type
#[component]
pub fn MimeTypeIcon(content_type:String, mime_type: String) -> AnyView {
    if content_type == "folder" {
        return folder_icon();
    }
    view! {
        {match mime_type.to_lowercase().as_str() {
            // Video MIME types
            "video/avi" => avi_icon(),
            "video/mov" => mov_icon().into_any(),
            "video/wmv" => wmv_icon().into_any(),
            "video/flv" => flv_icon().into_any(),
            "video/mpg" | "video/mpeg" => mpg_icon().into_any(),
            "video/mp4" => mp4_icon().into_any(),
            
            // Audio MIME types
            "audio/mp3" | "audio/mpeg" => mp3_icon().into_any(),
            "audio/mp4" | "audio/m4a" => m4a_icon().into_any(),
            "audio/aac" => aac_icon().into_any(),
            "audio/midi" => mid_icon().into_any(),
            "audio/wav" | "audio/wave" => audio_icon().into_any(),
            
            // Image MIME types
            "image/png" => png_icon().into_any(),
            "image/jpeg" | "image/jpg" => photo_icon().into_any(),
            "image/gif" => gif_icon().into_any(),
            "image/svg+xml" => svg_icon().into_any(),
            
            // Document MIME types
            "application/pdf" => pdf_icon().into_any(),
            "text/plain" => text_icon().into_any(),
            "text/html" => html_icon().into_any(),
            "text/css" => css_icon().into_any(),
            "text/xml" | "application/xml" => xml_icon().into_any(),
            "application/json" => json_icon().into_any(),
            "text/csv" => csv_icon().into_any(),
            "text/rtf" => rtf_icon().into_any(),
            "text/markdown" => markdown_icon().into_any(),
            
            // Microsoft Office
            "application/msword" | "application/vnd.openxmlformats-officedocument.wordprocessingml.document" => word_icon().into_any(),
            "application/vnd.ms-excel" | "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" => excel_icon().into_any(),
            "application/vnd.ms-powerpoint" | "application/vnd.openxmlformats-officedocument.presentationml.presentation" => powerpoint_icon().into_any(),
            "application/vnd.ms-access" => access_icon().into_any(),
            
            // Archive MIME types
            "application/zip" => zip_icon().into_any(),
            "application/x-7z-compressed" => zip7_icon().into_any(),
            "application/gzip" => gzip_icon().into_any(),
            "application/x-tar" => archive_icon().into_any(),
            "application/x-rar-compressed" => archive_icon().into_any(),
            "application/vnd.ms-cab-compressed" => cab_icon().into_any(),
            "application/x-debian-package" => deb_icon().into_any(),
            
            // Adobe Creative Suite
            "application/postscript" => ps_icon().into_any(),
            "application/illustrator" => ai_icon().into_any(),
            "application/x-photoshop" => ps_icon().into_any(),
            "application/x-indesign" => id_icon().into_any(),
            "application/x-aftereffects" => ae_icon().into_any(),
            "application/x-premiere" => pr_icon().into_any(),
            "application/x-lightroom" => lr_icon().into_any(),
            "application/x-animate" => an_icon().into_any(),
            "application/x-xd" => xd_icon().into_any(),
            "application/x-flash" => fla_icon().into_any(),
            
            // Data files
            "application/x-dbf" => dbf_icon().into_any(),
            "application/octet-stream" => dat_icon().into_any(),
            
            // Source code (generic)
            "text/x-source" => source_icon().into_any(),
            
            // Default fallback - check first part of MIME type
            _ => {
                if let Some(main_type) = mime_type.split('/').next() {
                    match main_type {
                        "video" => film_icon(),
                        "audio" => audio_icon().into_any(),
                        "text" => text_icon().into_any(),
                        "image" => image_icon().into_any(),
                        _ => document_icon().into_any(),
                    }
                } else {
                    document_icon().into_any()
                }
            },
        }}
    }
}
